mapper.update:
update user
set password = #{password}
where BINARY username = #{username};

update user
set online = 1
where BINARY username = #{username}
  and password = #{password};

update user
set online = 0
where BINARY username = #{username}
  and password = #{password};

mapper.select:
select *
from user
where BINARY username = #{username}
  and password = #{password}
  and ban = 0;

mapper.update:
update user
set base64 = #{password}
where BINARY username = #{username};

update user
set username = #{username}
where BINARY username = #{old_username};

update message
set sender = #{username}
where BINARY sender = #{old_username};

update user
set exclusiveColor = #{exclusiveColor}
where BINARY username = #{username};
